script:
local count,i;
addElement("size",0,"ushort");
addElement("opCode",2,"ushort");
count = addElement("count",4,"ushort");

for i = 1,count,1 do
  addElement("ver["..(i-1).."][0]",6+(i-1)*12,"uint");
  addElement("ver["..(i-1).."][1]",10+(i-1)*12,"uint");
  addElement("ver["..(i-1).."][2]",14+(i-1)*12,"uint");
end
