local count,nameStart,current,name;
addElement("size",0,"ushort");
addElement("opCode",2,"ushort");
count = addElement("count",4,"ushort");
addElement("shift1",6,"ushort");
addElement("shift2",8,"ushort");
  current = 53;
for i = 0, count -1,1 do
  nameStart = addElement("nameStart["..i.."]",current,"ushort");
  addElement("partyId["..i.."]",current+2,"ulong");
  addElement("id["..i.."]",current + 19,"ulong");
  addElement("level["..i.."]",current + 10,"ushort");
  addElement("class["..i.."]",current + 14,"byte");
  name = addElement("name["..i.."]",nameStart,"doubleString",nameStart+30);
  current = nameStart + (name+1)*2 + 4;
end
